questionvariables.text:
/*Question variables*/
radius: rand([5, 13, 10, 17, 15, 20, 25]);  /*Radius of the circle*/
c_x:rand_with_step(-5,5,1); /* The value of x at the center */
c_y:rand_with_step(-5,5,1); /* The value of y at the center */
a: rand([3, 5, 6, 8, 9, 12, 15]); 
b: a; 
while b = a do b: rand([3, 5, 6, 8, 9, 12, 15]); 
b;
c: a; 
while c = a or c = b do c: rand([3, 5, 6, 8, 9, 12, 15]); 
c;
aa:a;
bb:b;
cc:c;
a1:if aa=3 then 4 else if aa=5 then 12 else if aa=6 then 8 else if aa=8 then 15 else if aa=9 then 12 else if aa=12 then 16 else 20;
b1:if bb=3 then 4 else if bb=5 then 12 else if bb=6 then 8 else if bb=8 then 15 else if bb=9 then 12 else if bb=12 then 16 else 20;
c1:if cc=3 then 4 else if cc=5 then 12 else if cc=6 then 8 else if cc=8 then 15 else if cc=9 then 12 else if cc=12 then 16 else 20;
p_1_x:c_x+aa; /* The value of x at point p1 */
p_1_y:c_y+a1; /* The value of y at point p1 */
p_2_x:c_x+bb; /* The value of x at point p2 */
p_2_y:c_y+b1; /* The value of y at point p2 */
p_3_x:c_x+cc; /* The value of x at point p3 */
p_3_y:c_y+c1; /* The value of y at point p3 */
/*Model answers*/
ta1:sqrt((p_1_x-c_x)^2+(p_1_y-c_y)^2); /* Distance from point p1 to the center of the circle */
ta_1:if ta1>radius then random_permutation ([["inside the circle",false],["outside the circle",true],["on the circle",false]]) elseif ta1<radius then random_permutation ([["inside the circle",true],["outside the circle",false],["on the circle",false]]) else random_permutation ([["inside the circle",false],["outside the circle",false],["on the circle",true]]);
ta2:sqrt((p_2_x-c_x)^2+(p_2_y-c_y)^2); /* Distance from point p2 to the center of the circle */
ta_2:if ta2>radius then random_permutation ([["inside the circle",false],["outside the circle",true],["on the circle",false]]) elseif ta2<radius then random_permutation ([["inside the circle",true],["outside the circle",false],["on the circle",false]]) else random_permutation ([["inside the circle",false],["outside the circle",false],["on the circle",true]]);
ta3:sqrt((p_3_x-c_x)^2+(p_3_y-c_y)^2); /* Distance from point p3 to the center of the circle */
ta_3:if ta3>radius then random_permutation ([["inside the circle",false],["outside the circle",true],["on the circle",false]]) elseif ta3<radius then random_permutation ([["inside the circle",true],["outside the circle",false],["on the circle",false]]) else random_permutation ([["inside the circle",false],["outside the circle",false],["on the circle",true]]);
