questionvariables.text:
/* randomized components of the question */
center: [rand_with_prohib(-5,5,[0]), rand_with_prohib(-5,5,[0])];
const_a: rand(4)+1;
ecc: rand(2)+2;
axis_direction: rand(["x", "y"]);

/* determine key points */
prin_axis: if axis_direction="x" then y=center[2] 
  else if axis_direction="y" then x=center[1];

vertex1: if axis_direction="x" then center + [const_a,0] 
  else if axis_direction="y" then center + [0,const_a];
vertex2: if axis_direction="x" then center - [const_a,0] 
  else if axis_direction="y" then center - [0,const_a];

focus1: if axis_direction="x" then center + [ecc*const_a,0] 
  else if axis_direction="y" then center + [0,ecc*const_a];
focus2: if axis_direction="x" then center - [ecc*const_a,0] 
  else if axis_direction="y" then center - [0,ecc*const_a];

const: if ecc=2 then 3 else if ecc=3 then 8;
point: if axis_direction="x" then 
    center + [rand([1,-1])*ecc*const_a, rand([1,-1])*const*const_a]
  else if axis_direction="y" then
    center + [rand([1,-1])*const*const_a, rand([1,-1])*ecc*const_a];

/* points for q3 */
point1: random_permutation([rand([-1,1]), -1])*point;
point2: center + random_permutation([rand([-1,1]), -1])*(point-center);

/* --------------- REMOVED */ /*
I have removed the third part of (e) which should be a random point lying either on or off the hyperbola. This wasn't generating correctly so I am leaving it for now, possibly to be returned to later.

is_point3_corr: rand([true, false]);
point3_min_coord: rand_with_prohib(-4,4,[0, const, -const]);
point3_maj_coord: if is_point3_corr then 
    rand([-1,1])*sqrt(const_a^2 + point3_min_coord^2 / ecc^2)
  else
    rand([-1,1])*sqrt(rand_with_prohib(-5,5,[0]) + const_a^2 + point3_min_coord^2 / ecc^2);

point3: if axis_direction="x" then 
    center + [point3_maj_coord, point3_min_coord]
  else if axis_direction="y" then
    center + [point3_min_coord, point3_maj_coord];
*/ /* --------------- END OF REMOVED */

question_e: random_permutation([
  [point1, [["Yes", false], ["No", true]]],
  [point2, [["Yes", true], ["No", false]]]
  /*[point3, [["Yes", is_point3_corr], ["No", not(is_point3_corr)]]]*/
]);

/* teacher answers */
ta1x:center[1];
ta1y:center[2];

ta2:prin_axis;

ta3:2*const_a;

ta4x:vertex1[1];
ta4y:vertex1[2];
ta5x:vertex2[1];
ta5y:vertex2[2];

ma6a:question_e[1][2];
ma6b:question_e[2][2];
/*ma6c:question_e[3][2];*/
ta6a:mcq_correct(ma6a)[1];
ta6b:mcq_correct(ma6b)[1];
/*ta6c:mcq_correct(ma6c)[1];*/

/* worked solution */
simp:false;

line1: sqrt((point[1] - focus1[1])^2 + (point[2] - focus1[2])^2);
line2: sqrt((point[1] - focus2[1])^2 + (point[2] - focus2[2])^2);

line1e: [
    (question_e[1][1][1] - focus1[1])^2 + (question_e[1][1][2] - focus1[2])^2,
    (question_e[2][1][1] - focus1[1])^2 + (question_e[2][1][2] - focus1[2])^2
  /*(question_e[3][1][1] - focus1[1])^2 + (question_e[3][1][2] - focus1[2])^2*/
];

line2e: [
    (question_e[1][1][1] - focus2[1])^2 + (question_e[1][1][2] - focus2[2])^2,
    (question_e[2][1][1] - focus2[1])^2 + (question_e[2][1][2] - focus2[2])^2
  /*(question_e[3][1][1] - focus2[1])^2 + (question_e[3][1][2] - focus2[2])^2*/
];

simp:true;

line3e: makelist(ev(expand(line1e[i]), simp), i, 1, 2);
line4e: makelist(ev(expand(line2e[i]), simp), i, 1, 2);
line5e: makelist(ev(abs(sqrt(line3e[i])-sqrt(line4e[i])), simp), i, 1, 2);
