questionvariables.text:
/*Question Variables*/
coef_x:rand(5)+2;
coef_y:rand(6)+coef_x+7;
constant:coef_x+rand(2);
point_x: rand(2)+2;
y_value: (coef_x*point_x+constant)/coef_y; /*Given point_x, we always have the point on the line*/
point_y: rand_with_prohib(3,9,[y_value]);
line_eq:coef_x*x+coef_y*y-constant=0;
/*worked solution variables*/
soln1:coef_x*point_x+coef_y*point_y-constant;
soln2:sqrt((coef_x)^2+(coef_y)^2);
ta1:soln1/soln2;

feedbackvariables.text:
/*node 1:checks for the correct answer*/

/*node 2:Failing to find the square root of the sum of A^2+B^2 in the denominator of the formulae of finding the distance by using it as shown below \frac{\left | Ah+Bk+C \right |}{\sqrt{A^2+B^2}}*/
mistake_1:((coef_x*point_x+coef_y*point_y-constant)/((coef_x)^2+(coef_y)^2));

/*node 3:Failing to square A and B in the denominator of the formula And using as shown below \frac{\left | Ah+Bk+C \right |}{\sqrt{A^2+B^2}}*/
mistake_2:((coef_x*point_x+coef_y*point_y-constant)/(sqrt(coef_x+coef_y)));
