questionvariables.text:
Angle_of_rot:rand([30,45,60,90,180,270,360]);
Angle_in_pi:if Angle_of_rot=30 then pi/6 else if Angle_of_rot=45 then pi/4 else if Angle_of_rot=60 then pi/3 else if Angle_of_rot=90 then pi/2 else if Angle_of_rot=180 then pi else if Angle_of_rot=270 then (3*pi)/2 else 2*pi;
x1:rand_with_step(-16,16,1);
x_a:if x1>0 then sqrt(x1) else if x1<0 then -sqrt(abs(x1)) else 0;
y1:rand_with_step(-16,16,1);
y_a:if y1>0 then sqrt(y1) else if y1<0 then -sqrt(abs(y1)) else 0;
x2:rand_with_step(-16,16,1);
x_b:if x2>0 then sqrt(x2) else if x2<0 then -sqrt(abs(x2)) else 0;
y2:rand_with_step(-16,16,1);
y_b:if y2>0 then sqrt(y2) else if y2<0 then -sqrt(abs(y2)) else 0;
x3:rand_with_step(-16,16,1);
x_c:if x3>0 then sqrt(x3) else if x3<0 then -sqrt(abs(x3)) else 0;
y3:rand_with_step(-16,16,1);
y_c:if y3>0 then sqrt(y3) else if y3<0 then -sqrt(abs(y3)) else 0;
x4:rand_with_step(-16,16,1);
x_d:if x4>0 then sqrt(x4) else if x4<0 then -sqrt(abs(x4)) else 0;
y4:rand_with_step(-16,16,1);
y_d:if y4>0 then sqrt(y4) else if y4<0 then -sqrt(abs(y4)) else 0;
a1:if Angle_of_rot=30 then cos(pi/6) else if Angle_of_rot=45 then cos(pi/4) else if Angle_of_rot=60 then cos(pi/3) else if Angle_of_rot=90 then cos(pi/2) else if Angle_of_rot=180 then cos(pi) else if Angle_of_rot=270 then cos((3*pi)/2) else cos(2*pi);
a2:if Angle_of_rot=30 then sin(pi/6) else if Angle_of_rot=45 then sin(pi/4) else if Angle_of_rot=60 then sin(pi/3) else if Angle_of_rot=90 then sin(pi/2) else if Angle_of_rot=180 then sin(pi) else if Angle_of_rot=270 then sin((3*pi)/2) else sin(2*pi);
a3:if Angle_of_rot=30 then cos(-pi/6) else if Angle_of_rot=45 then cos(-pi/4) else if Angle_of_rot=60 then cos(-pi/3) else if Angle_of_rot=90 then cos(-pi/2) else if Angle_of_rot=180 then cos(-pi) else if Angle_of_rot=270 then cos((-3*pi)/2) else cos(-2*pi);
a4:if Angle_of_rot=30 then sin(-pi/6) else if Angle_of_rot=45 then sin(-pi/4) else if Angle_of_rot=60 then sin(-pi/3) else if Angle_of_rot=90 then sin(-pi/2) else if Angle_of_rot=180 then sin(-pi) else if Angle_of_rot=270 then sin((-3*pi)/2) else sin(-2*pi);
x_1:ratsimp(x_a*a3-y_a*a4);
y_1:ratsimp(x_a*a4+y_a*a3);
x_2:ratsimp(x_b*a3-y_b*a4);
y_2:ratsimp(x_b*a4+y_b*a3);
x_3:ratsimp(x_c*a3-y_c*a4);
y_3:ratsimp(x_c*a4+y_c*a3);
x_4:ratsimp(x_d*a3-y_d*a4);
y_4:ratsimp(x_d*a4+y_d*a3);
ta1:[x_1,y_1];
ta2:[x_2,y_2];
ta3:[x_3,y_3];
ta4:[x_4,y_4];
