questionvariables.text:
/* Define the rotation angle in radians */
angle:rand_with_step(30,1500,30);
theta: angle*%pi/180;  
/* Define the rotation matrix */
rotation_matrix: matrix([cos(theta), -sin(theta)], [sin(theta), cos(theta)]);
/* Define the points in the xy-system */
points_xy: [ [rand(5)+5, rand(5)], [rand(9)+1, rand(4)], [rand(10), rand(4)+1], [-1*(rand(5)+1)/(rand(5)+2), (rand(9)+1)/(rand(4)+2)], [(-1*rand(4)+1), (-1*(rand(8)+1))] ];  
/* Initialize a list to store the results */ points_xyp: [];  
/* Loop through each point and apply the rotation transformation */
for p in points_xy do (	 
	x_new: p[1]*rotation_matrix[1][1] + p[2]*rotation_matrix[1][2],	 
	y_new: p[1]*rotation_matrix[2][1] + p[2]*rotation_matrix[2][2],	 
	points_xyp: append(points_xyp, [x_new, y_new]) );  
/* Display the results */
p:points_xyp;
/*Model answers*/
ta1:matrix([p[1]],[p[2]]);
ta2:matrix([p[3]],[p[4]]);
ta3:matrix([p[5]],[p[6]]);
ta4:matrix([p[7]],[p[8]]);
ta5:matrix([p[9]],[p[10]]);

feedbackvariables.text:
/* Define the rotation angle in radians */
angle:rand_with_step(30,1500,15);
theta: angle*%pi/180;  
/* Define the rotation matrix */
rotation_matrix: matrix([cos(theta), +sin(theta)], [sin(theta), cos(theta)]);
/* Define the points in the xy-system */
points_xy: [ [rand(5)+5, rand(5)], [rand(9)+1, rand(4)], [rand(10), rand(4)+1], [-1*(rand(5)+1)/(rand(5)+2), (rand(9)+1)/(rand(4)+2)], [(-1*rand(4)+1), (-1*(rand(8)+1))] ];  
/* Initialize a list to store the results */ points_xyp: [];  
/* Loop through each point and apply the rotation transformation */
for p in points_xy do (	 
	x_new: p[1]*rotation_matrix[1][1] + p[2]*rotation_matrix[1][2],	 
	y_new: p[1]*rotation_matrix[2][1] + p[2]*rotation_matrix[2][2],	 
	points_xyp: append(points_xyp, [x_new, y_new]) );  
/* Display the results */
p:points_xyp;
/*Model answers*/
mistake1:matrix([p[1]],[p[2]]);
