questionvariables.text:
angdeg:rand_with_prohib(20,70,[30,45,60]);
angrad:float(angdeg*%pi/180);
angname:string(angdeg);
fx:rand([sin(x),cos(x),tan(x)]);
yval:float(ev(fx,x=angrad));
ta1:if fx=sin(x) then angdeg+360 elseif fx=cos(x) then 360+angdeg else 180+angdeg;
ta2:if fx=sin(x) then angdeg-360 elseif fx=cos(x) then angdeg-360 else -180+angdeg;
ta3:if fx=sin(x) then 180-angdeg elseif fx=cos(x) then -angdeg else angdeg+360;

feedbackvariables.text:
setans:{ans1,ans2,ans3};
distans:cardinality(intersect(setans,{angdeg}));
numans:cardinality(setans);
ans12:float(ans1*%pi/180);
ans22:float(ans2*%pi/180);
ans32:float(ans3*%pi/180);
setanstest1:{ev(fx,x=ans12),ev(fx,x=ans22),ev(fx,x=ans32)};
setanstest:member(yval, setanstest1);
setta:{ta1,ta2,ta3};
/*Node 1: Checks if the student setanswers are correct*/
/*Node 2: Checks for the cardinality of the student answers*/
/*Node 3: Checks for the cardinality of the intersection between the student answers and the given angle */
/*Node 4:Checks if the wrong student answer is the sine of the given angle*/
/*Node 5:Checks if the wrong student answer is the cosine of the given angle*/
/*Node 6:Checks if the wrong student answer is the tangent of the given angle*/
