questionvariables.text:
ordergreat(m,n);

/*Randomization {(aa*m*n, bb*m + cc*n)}*/
aa:rand(5)+1;
bb:rand(5)+1;
cc:rand(5)+1;

if bb + 6*cc # 2*bb + 3*cc then (
    t_ans1:1,
    t_ans1s:6,
    t_ans2:2,
    t_ans2s:3
) else (
    t_ans1:1,
    t_ans1s:8,
    t_ans2:2,
    t_ans2s:4
);

feedbackvariables.text:
/* Node 1 - Checks all inputs are integers */
node1_test: if (integerp(ans1) and integerp(ans1s) and integerp(ans2) and integerp(ans2s)) then 1 else 0;

/* Node 2 - Check product of (m,n) pairs match */
node2_test: if ans1*ans1s = ans2*ans2s then 1 else 0;

/* Node 3 - Check values in domain differ (for n1-T) */
/* Node 4 - Check values in domain differ (for n1-F) */
node3_test: if bb*ans1 + cc*ans1s # bb*ans2 + cc*ans2s then 1 else 0;
