questionvariables.text:
/*Question 1 variables*/
aa:rand(5)+1;
bb:aa+1;
n1:aa^2;   /*first value of n*/
n2:bb^2; /*second value of n*/
eqn:(n-n1)*(n-n2); 
eqn1:expand(eqn);
cc:coeff(eqn1, n, 0);
difference: sqrt(cc);
ta1: if n1-sqrt(n1)=difference then n1 else n2;
wrong_sol: if n1-sqrt(n1)=difference then n2 else n1;

/*Question 2 variables*/
x2:rand(10)+13;
x1:x2+4;
expr:(x-x2)*(x-x1);
expr1:expand(eqn1);
c:x1-5; hypotenuse:x-c;
a:x1-4; height:x-a;
b:x1-3; base:x-b;
expr2:height^2+base^2=hypotenuse^2;
expr3:expand(height^2)+expand(base^2)=expand(hypotenuse^2);
expr4:expand(height^2+base^2-hypotenuse^2);
expr5:solve(expr4=0);
ta2:x1;

feedbackvariables.text:
/*node 1 checks for correct answer*/
/*node 2 mistake: inputting the wrong value of x*/
mistake_2:x2;
