questionvariables.text:
ordergreat(x,y,z);

tax:rand(11)-5;
tay:rand(11)-5;
taz:rand(11)-5;

cx1:rand(5)+1;
cx2:rand_with_prohib(1,5,[cx1]);
cx3:rand_with_prohib(1,5,[cx1]);

pmcy1:rand([-1,1]);
cy1:pmcy1*(rand(6)+1);
sigcy1:if cy1>0 then "+" else "-";
pmcy2:rand([-1,1]);
cy2:pmcy2*(rand(6)+1);
sigcy2:if cy2>0 then "+" else "-";
cy3:rand_with_prohib(-6,6,[cy2,0]);
sigcy3:if cy3>0 then "+" else "-";

pmcz1:rand([-1,1]);
cz1:pmcz1*(rand(4)+1);
sigcz1:if cz1>0 then "+" else "-";
cz2:rand_with_prohib(-4,4,[cz1,0]);
sigcz2:if cz2>0 then "+" else "-";
cz3:rand_with_prohib(-4,4,[cz2,0]);
sigcz3:if cz3>0 then "+" else "-";

res1:cx1*tax+cy1*tay+cz1*taz;
res2:cx2*tax+cy2*tay+cz2*taz;
res3:cx3*tax+cy3*tay+cz3*taz;

lhs1:cx1*x+cy1*y+cz1*z;
lhs2:cx2*x+cy2*y+cz2*z;
lhs3:cx3*x+cy3*y+cz3*z;

eqn1:lhs1=res1;
eqn2:lhs2=res2;
eqn3:lhs3=res3;

ta1:matrix([cx1,cy1,cz1],[cx2,cy2,cz2],[cx3,cy3,cz3]);

ta2:matrix([res1],[res2],[res3]);

ta3:determinant(ta1);

ta4: matrix([res1,cy1,cz1],[res2,cy2,cz2],[res3,cy3,cz3]);

ta5:determinant(ta4);

ta6:ta5/ta3;

ta7: matrix([cx1,res1,cz1],[cx2,res2,cz2],[cx3,res3,cz3]);

ta8:determinant(ta7);

ta9:ta8/ta3;

ta10: matrix([cx1,cy1,res1],[cx2,cy2,res2],[cx3,cy3,res3]);

ta11:determinant(ta10);

ta12:ta11/ta3;
