questionvariables.text:
aa:rand(3)+1;
bb:rand_with_prohib(4,8,[aa]);
cc:rand_with_prohib(-5,5,[aa,bb,0]);
dd:rand_with_step(1,2,8);
ee:rand([2,4,6]);
matrix_A:matrix([0,0,aa,bb],[0,aa,0,dd],[0,dd,dd,ee]);/*matrix A*/
ta:rref(matrix_A);/*reduced row echelon form*/


/*Worked solution*/
matrix_1:matrix([0,dd,dd,ee],[0,aa,0,dd],[0,0,aa,bb]);/*Interchanging row 1 and 3*/
matrix_2:matrix([0,dd,dd,ee],[0+(-aa*0),aa+(-aa*dd),0+(-aa*dd),dd+(-aa*ee)],[0,0,aa,bb]);
ww:1/(0+(-aa*dd));
ww_1:0+(-aa*dd);
ww_2:ww*(0+(-aa*dd));
ww_3:ww*(dd+(-aa*ee));
ww_4:1/(bb+(ww_1*ww_3));
ww_5:aa+(ww_1*ww_2);
ww_6:ww_4*(bb+(ww_1*ww_3));
ww_7:-ww_3;
ww_8:-1*(dd+(-ee*ww_5));
ww_9:ww*(0+(-aa*0))+ww_7*0;
ww_10:ww*(aa+(-aa*dd))+ww_7*0;
ww_11:ww*(0+(-aa*dd))+ww_7*ww_5;
w_12:ww*(dd+(-aa*ee))+ww_7*ww_6;
matrix_3:matrix([0,dd,dd,ee],[ww*(0+(-aa*0)),ww*(aa+(-aa*dd)),ww*(0+(-aa*dd)),ww*(dd+(-aa*ee))],[0,0,aa,bb]);
matrix_4:matrix([0,dd,dd,ee],[ww*(0+(-aa*0)),ww*(aa+(-aa*dd)),ww*(0+(-aa*dd)),ww*(dd+(-aa*ee))],[0,0,aa+(ww_1*ww_2),bb+(ww_1*ww_3)]);
matrix_5:matrix([0,dd,dd,ee],[ww*(0+(-aa*0)),ww*(aa+(-aa*dd)),ww*(0+(-aa*dd)),ww*(dd+(-aa*ee))],[0,0,(aa+ww_1)*ww_2,ww_4*(bb+(ww_1*ww_3))]);
matrix_6:matrix([0+(-ee*0),dd+(-ee*0),dd+(-ee*ww_5),ee+(-ee*ww_6)],[ww*(0+(-aa*0))+ww_7*0,ww*(aa+(-aa*dd))+ww_7*0,ww*(0+(-aa*dd))+ww_7*ww_5,ww*(dd+(-aa*ee))+ww_7*ww_6],[0,0,(aa+ww_1)*ww_2,ww_4*(bb+(ww_1*ww_3))]);
matrix_7::matrix([0+(-ee*0)+ww_8*ww_9,dd+(-ee*0)+ww_8*ww_10,dd+(-ee*ww_5)+ww_8*ww_11,ee+(-ee*ww_6)+ww_8*w_12],[ww*(0+(-aa*0))+ww_7*0,ww*(aa+(-aa*dd))+ww_7*0,ww*(0+(-aa*dd))+ww_7*ww_5,ww*(dd+(-aa*ee))+ww_7*ww_6],[0,0,(aa+ww_1)*ww_2,ww_4*(bb+(ww_1*ww_3))]);
