questionvariables.text:
ta_true: rand_selection([
  [1, true, "Horses are mammals and frogs are amphibians."],
  [2, true, "The sum of the interior angles of any triangle is always 180 degrees."],
  [3, true, "if two lines are parallel, then they will never intersect"]
], 1);

ta_false: rand_selection([
  [4, false, "\\(2 \\times 4 = 8\\), only if \\(2 + 4 = 8\\)"],
  [5, false, "Division by zero is defined and results in a meaningful value"],
  [6, false, "The square root of a negative number is a real number."]
], 2);

ta:random_permutation(append(ta_true,ta_false));
ta_index: sort(makelist(x[1], x, ta));
ta_largest: last(ta_index);
