questionvariables.text:
/* Defining the question information as a matrix */
/* [*operator name*, *latex symbol*, *word*, [*truth table*], *position in statement matrix lists*] */

operator: rand([
  ["a conjunction", "\\land", "and", ["true", "false", "false", "false"], 1], 
  ["a disjunction", "\\lor", "or", ["true", "true", "true", "false"], 2],
  ["an implication", "\\Rightarrow", "implies that", ["true", "false", "true", "true"], 3],
  ["a bi-implication", "\\iff", "if and only if", ["true", "false", "false", "true"], 4]
]);

statement_A: rand({["it is raining", 1], ["it is not raining", 2]});
statement_B: rand({["I am telling the truth", 1], ["I am not telling the truth", 2]});

/* Truth table inputs */

m_ans1: [
  ["true", is(operator[4][1]="true")],
  ["false", is(operator[4][1]="false")]
];
t_ans1: operator[4][1];

m_ans2: [
  ["true", is(operator[4][2]="true")],
  ["false", is(operator[4][2]="false")]
];
t_ans2: operator[4][2];

m_ans3: [
  ["true", is(operator[4][3]="true")],
  ["false", is(operator[4][3]="false")]
];
t_ans3: operator[4][3];

m_ans4: [
  ["true", is(operator[4][4]="true")],
  ["false", is(operator[4][4]="false")]
];
t_ans4: operator[4][4];

/* Statement inputs */
/* 
We store the truth evaluations of each possible statement as a matrix of lists, each position corresponds to a possible statement and each entry a list of the truth for [*conjunction*, *disjunction*, *implication*, *bi-implication*]

                     |  "raining"  | "not raining"
-----------------------------------------------------
"telling truth"      |    [***]    |    [***]   
-----------------------------------------------------
"not telling truth"  |    [***]    |    [***]   


*/

statement_matrix: [
  [[4, 1, 4, 4], [2, 4, 1, 3]],
  [[3, 1, 3, 3], [2, 3, 1, 4]]
];

m_ans5: [
  [1, is(statement_matrix[statement_B[2]][statement_A[2]][operator[5]]=1), "This statement is true"],
  [2, is(statement_matrix[statement_B[2]][statement_A[2]][operator[5]]=2), "This statement is false"],
  [3, is(statement_matrix[statement_B[2]][statement_A[2]][operator[5]]=3), "This statement is neither true nor false since each option gives a contradiction"],
  [4, is(statement_matrix[statement_B[2]][statement_A[2]][operator[5]]=4), "This statement could be either true or false"]
];

t_ans5: if m_ans5[1][2] then 1
  else if m_ans5[2][2] then 2
  else if m_ans5[3][2] then 3
  else if m_ans5[4][2] then 4;

/* Variables for question tests */

/* (a) */

/* Node 3: Check whether table fits a different connective (test using a conjunction for disj. or impl., and a disjunction for conj. or bi-impl. to ensure more than one row is incorrect) */
part_a_n3_1: if operator[5]=1 or operator[5]=4 then ["true", "true", "true", "false"] else ["true", "false", "false", "false"];

feedbackvariables.text:
/* Node 1: Check truth table entirely correct */
node1_test: if [ans1,ans2,ans3,ans4]=[t_ans1,t_ans2,t_ans3,t_ans4] then 1 else 0;

/* Node 2: Three of four rows correct */
node2_test: if 
    [ans1,ans2,ans3]=[t_ans1,t_ans2,t_ans3]
    or [ans1,ans2,ans4]=[t_ans1,t_ans2,t_ans4]
    or [ans1,ans3,ans4]=[t_ans1,t_ans3,t_ans4]
    or [ans2,ans3,ans4]=[t_ans2,t_ans3,t_ans4]
  then 1 else 0;

/* Node 3: Table fits different connective */
node3_test: if elementp(makelist(i[4], i, operators)) then 1 else 0;
node3_feedback: if 
    [ans1,ans2,ans3,ans4]=["true", "false", "false", "false"]
  then "a conjunction" else if 
    [ans1,ans2,ans3,ans4]=["true", "true", "true", "false"]
  then "a disjunction" else if 
    [ans1,ans2,ans3,ans4]=["true", "false", "true", "true"]
  then "an implication" else if 
    [ans1,ans2,ans3,ans4]=["true", "false", "false", "true"]
  then "a bi-implication" else "";

/* Node 2: Claims the statement is true when it cannot be true */
node2_test: if ans5=1 and (t_ans5=2 or t_ans5=3) then 1 else 0;

/* Node 3: Claims the statement is true when it can be true or false */
node3_test: if ans5=1 and t_ans5=4 then 1 else 0;

/* Node 4: Claims the statement is false when it cannot be false */
node4_test: if ans5=2 and (t_ans5=1 or t_ans5=3) then 1 else 0;

/* Node 5: Claims the statement is false when it can be true or false */
node5_test: if ans5=2 and t_ans5=4 then 1 else 0;

/* Node 6: Incorrectly claims the statement is neither true nor false */
node6_test: if ans5=3 then 1 else 0;
node6_feedback: if t_ans5=1 then "true" else "false";

/* Node 7: Incorrectly claims the statement could be either true or false */
node7_test: if ans5=4 then 1 else 0;
node7_feedback: if t_ans5=1 then "false" else "true";
