questionvariables.text:
/* For each of the 4 quadrants, there are 3 different argument values to choose from so that the complex number has nice values in both cartesian and polar form: https://en.wikipedia.org/wiki/List_of_trigonometric_identities#/media/File:Unit_circle_angles_color.svg */
quadrant: rand(4);

angle: rand([%pi/2, %pi/3, %pi/4, %pi/6]);
arg: if -%pi<(angle+%pi/2*quadrant) and (angle+%pi/2*quadrant)<=%pi then (angle+%pi/2*quadrant) else (angle+%pi/2*quadrant)-2*%pi;
radius: rand_with_step(1, 9, 1);
zz: radius*%e^(%i*arg);

/*zz_cartesian does not always display the real part first then the imaginary part*/
z_real: realpart(zz);
z_imag: imagpart(zz);
zz_cartesian: z_real + z_imag * %i;

/* polarform(zz) does not give the complex number in the form zz (above) in fact it gives the complex number in cartesian form!*/
ta1: radius;
ta2: arg;

simp:false;
ta3: radius*%e^(ta2*%i);

/*cube roots*/
wk: %e^(((ta2+2*k*%pi)/3)*%i);
arg4: if -%pi<(arg/3) and (arg/3)<=%pi then ev((arg/3),simp) else ev((arg/3)-2*pi,simp);
ta4: radius^(1/3)*(cos(arg4)+%i *sin(arg4));

arg5: if -%pi<((arg+2*%pi)/3) and ((arg+2*%pi)/3)<=%pi then ev(((arg+2*%pi)/3),simp) else ev(((arg+2*%pi)/3)-2*pi,simp);
ta5: radius^(1/3)*(cos(arg5)+%i* sin(arg5));

arg6: if -%pi<((arg+4*%pi)/3) and ((arg+4*%pi)/3)<=%pi then ev(((arg+4*%pi)/3),simp) else ev(((arg+4*%pi)/3)-2*pi,simp);
ta6: radius^(1/3)*(cos(arg6)+%i* sin(arg6));
teacher_roots: {ta4, ta5, ta6}; /* Set of cube roots for roots z*/

feedbackvariables.text:
/*Node 1: Checks for the correct value of r */
/*Node 2: Checks for the correct measured angle from the positive real axis*/
/*Node 3: Checks for the correct polar form using the incorrect values for r and theta */
/*Node 4: Checks for the correct polar form*/

simp: true;

/*student polar form for z*/
s_radius: ans1;
s_arg: ans2;
s_zz: s_radius*%e^(%i*s_arg);

student_roots: {ans4, ans5, ans6}; /* Make a SET of student answers (set means that duplicates are removed) */

correct_answers: intersection(student_roots,teacher_roots); 
number_correct: cardinality(correct_answers);

/* student calculation for the square root */
s_ans4: s_radius^(1/3)*(cos((s_arg)/3) + %i *sin((s_arg)/3));
s_ans5: s_radius^(1/3)*(cos((s_arg+2*%pi)/3) + %i* sin((s_arg+2*%pi)/3));
s_ans6: s_radius^(1/3)*(cos((s_arg+4*%pi)/3) + %i* sin((s_arg+4*%pi)/3));

/* Proof of concept of using set comparison and carrying student errors forward. */
/* This is VERY crude and needs polishing. */
student_roots_based_on_their_polar_form: {s_ans4, s_ans5, s_ans6};

/*Node 1: Checks if all the three roots are correct */
/*Node 2: Checks if two roots are correct*/
/*Node 3: Checks if one root is correct*/
/*Node 4: Checks for the correct cube root of the polar form thou with incorrect polar form */
