questionvariables.text:
/* Define the complex number */
a:rand(50)+1;
b:rand_with_prohib(-50,50,[0,a]);
num:(a + b*%i);
den:(-b*%i);
den_conj:conjugate(den);
z: num/den;


/* Simplify the complex number */
z_simplified: ratsimp(z);

/* Multiply by conjugate to get rid of the denominator */
num1:num*den_conj;
den1:den*den_conj;
z_conjugate:num1/den1;

/* Simplify the conjugate expression */
z_conjugate_simplified: (num1/den1);

/* Separate real and imaginary parts */
re_z: realpart(z_conjugate_simplified);
im_z: imagpart(z_conjugate_simplified);

/* Calculate the modulus */
mod: sqrt(re_z^2 + im_z^2);

/* Print the result */
ta:mod;
