questionvariables.text:
/* for the first point, randomise two distinct non-zero coordinates (so point 1 cannot be the same as 2 or 3) */
point1x: rand_with_prohib(-5,5,[0]);
point1y: rand_with_prohib(-5,5,[0]);

/* for the second and third points, randomise the angle and then the radius */
axes_angles: [-pi/2, pi/2, pi, 3*pi/2, 2*pi];
diag_angles: [-pi/4, pi/4, 3*pi/4, 5*pi/4, 7*pi/4];

angles: random_permutation([rand(axes_angles), rand(diag_angles)]);

point2_rad: if member(angles[1], axes_angles) then rand(5)+1 else if member(angles[1], diag_angles) then sqrt(2*(rand(5)+1)^2);
point3_rad: if member(angles[2], axes_angles) then rand(5)+1 else if member(angles[2], diag_angles) then sqrt(2*(rand(5)+1)^2);

/* teacher answers */
ta1:[point1x, point1y];
ta2:[point2_rad*cos(angles[1]), point2_rad*sin(angles[1])];
ta3:[point3_rad*cos(angles[2]), point3_rad*sin(angles[2])];

feedbackvariables.text:
/* checks on first answer */
node2_test: if member(ans1, [ta1, ta2, ta3]) then 1 else 0;

/* checks on second answer */
node3_test: if ans2=ans1 then 1 else 0;
node4_test: if member(ans2, [ta1, ta2, ta3]) then 1 else 0;

/* checks on third answer */
node5_test: if ans3=ans1 or ans3=ans2 then 1 else 0;
node6_test: if member(ans3, [ta1, ta2, ta3]) then 1 else 0;
