questionvariables.text:
quadrant: rand(4);

angle: rand([%pi/2, %pi/3, %pi/4, %pi/6]);

arg: angle + %pi/2*quadrant;
radius: rand_with_step(1, 9, 1);

zz: radius*%e^(%i*arg);

z_real: realpart(zz);
z_imag: imagpart(zz);
simp: false;
zz_cartesian: z_real + z_imag * %i;
simp: true;
/*Model Answers*/
ta1: radius;
ta2: arg;
ta3:  radius*(cos(arg) + %i *sin(arg));/*z in polar form*/

/*roots*/
ta4: sqrt(radius)*(cos(arg/2) + %i *sin(arg/2));
ta5: sqrt(radius)*(cos((arg+2*%pi)/2) + %i* sin((arg+2*%pi)/2));
ta_roots: {ta4, ta5}; /* teacher square roots*/

/*Worked solution codes*/

feedbackvariables.text:
ans_roots: {ans4, ans5}; /* student square roots*/
ta_roots: {ta4, ta5}; /* teacher square roots*/
student_ans: is(ans_roots=ta_roots);

err:if cardinality(intersect(ans_roots,ta_roots))>0 then 1 else 0;
errcorr:if cardinality(intersect(ans_roots,ta_roots))>0 then listify(intersect(ans_roots,ta_roots))[1] else "";
